import "./App.css";
import 'bootstrap/dist/css/bootstrap.css';
import Navbar from "./Components/Navbar";
import TextForm from "./Components/TextForm";
import About from "./Components/About";
import React, { useState } from "react";
import Joystick from "./Components/joystick";
import Alert from "./Components/Alert";

// import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

function App() {
  const [mode, setmode] = useState("light");
  const [alert, setalert] = useState(null);
  const showAlert = (message, type) => {
    setalert({ msg: message, type: type });
    setTimeout(() => {
      setalert(null);
    }, 1500);
  };
  const togglemode = () => {
    if (mode === "light") {
      setmode("dark");
      document.body.style.backgroundColor = "#25282F";
      showAlert("dark mode has been enabled", "success");
    } else {
      setmode("light");
      document.body.style.backgroundColor = "white";
      showAlert("dark mode has been disable", "success");
    }
    let i = 5;
    setInterval(() => {
      i % 2 === 1
        ? (document.title = "TextUtil")
        : (document.title = "TextEditor");
      console.log("called");
      i = (i + 1) % 5;
    }, 1000);
  };

  const red = () => {
    console.log("redify");
    setmode("red");
    document.body.style.backgroundColor = "#ff0909";
    showAlert("red mode has been enable", "success");
  };

  const blue = () => {
    console.log("bluifie");
    setmode("blue");
    document.body.style.backgroundColor = "#7700ff";
    showAlert("blue mode is enable ", "success");
  };
  return (
    <>
      {/* <Router> */}
        <link rel="icon" href="%PUBLIC_URL%/logo.png" />
        <Navbar
          title="textutil2"
          abouttext="AbOut"
          mode={mode}
          togglemode={togglemode}
          red={red}
          blue={blue}
        />
        <Alert alert={alert}></Alert>
    </>
  );
}

export default App;


